// Gemini'den gelen veriyi al
let rawContent = "";

try {
  if (items[0].json.candidates && items[0].json.candidates[0].content) {
    rawContent = items[0].json.candidates[0].content.parts[0].text;
  } else if (items[0].json.text) {
    rawContent = items[0].json.text;
  } else {
    rawContent = JSON.stringify(items[0].json);
  }
} catch (error) {
  throw new Error("Gemini verisi okunamadƒ±.");
}

// Temizlik
rawContent = rawContent.replace(/```json/g, "").replace(/```/g, "").trim();

// JSON D√ºzeltme (Eƒüer Gemini sonunda } unuttuysa)
if (!rawContent.endsWith('}')) {
    const lastClose = rawContent.lastIndexOf('}');
    if (lastClose !== -1) {
        rawContent = rawContent.substring(0, lastClose + 1);
    } else {
        // Acil durum: Elle kapat
        rawContent += '}}}'; 
    }
}

let parsedData = {};
try {
  parsedData = JSON.parse(rawContent);
} catch (e) {
    // √áok b√ºy√ºk ihtimalle eksik virg√ºl veya parantez var
    // Basit bir temizlik daha yapalƒ±m
    try {
        // Son virg√ºl√º silmeyi dene
        rawContent = rawContent.replace(/,(\s*})/, '$1');
        parsedData = JSON.parse(rawContent);
    } catch (e2) {
        throw new Error("JSON parse hatasƒ±: " + rawContent);
    }
}

const webhookData = $('Webhook').item.json.body;

// Eƒüer Gemini'den gelen veride eksik varsa, varsayƒ±lan deƒüerlerle tamamla (Hata √∂nleyici)
if (!parsedData.metadata) parsedData.metadata = { title: webhookData.businessName, description: "" };
if (!parsedData.theme) parsedData.theme = { colors: { primary: "#000", secondary: "#333", background: "#fff", text: "#000" }, fonts: { heading: "Inter", body: "Inter" }, style: "modern" };
if (!parsedData.images) parsedData.images = { hero_keyword: "business", about_keyword: "team", feature_keywords: [] };
if (!parsedData.content) parsedData.content = { hero: {}, about: {}, services: [], testimonials: [], contact: {} };

// Repo ismini olu≈ütur
const repoName = 'site-' + webhookData.customDomain.replace(/\./g, '-').toLowerCase().trim();

return [
  {
    json: {
